    /// <summary>
    /// 登录按钮事件
    /// </summary>
    private async void Btn_LR()
    {
        Login(Refs.Get<TMP_InputField>("IF_PhoneNumber").text);//测试
        //if (Application.dataPath.EndsWith("MetaCJ/Assets"))
        //{
        //    Login("10010");//测试
        //}
        //else
        //{
        //    Login("10086");//测试
        //}
        return;
        if (waitHttp) return;

        //检查是否知悉并同意协议
        if (!AgreeAll)
        {
            if (!AgreePrivacy && !AgreeUser) 
            {
                Debug.Log("登录按钮事件:弹出提示面板");
                UIManager.Instance.Push(new TipsPanel());
                UIManager.Instance.UI_GetGameObject("Tips").GetComponent<TMP_Text>().text = "请先同意相关协议";
            }
            if (!AgreePrivacy && AgreeUser)
            {
                Debug.Log("登录按钮事件:弹出消息面板");
                UserInterface.Instance.ShowMessage(new MessagePanel(), "尚未同意隐私协议");
            }
            if (AgreePrivacy && !AgreeUser)
            {
                Debug.Log("登录按钮事件:弹出消息面板");
                UserInterface.Instance.ShowMessage(new MessagePanel(), "尚未同意用户协议");
            }
        }
        else
        {
            var phoneNum = Refs.Get<TMP_InputField>("IF_PhoneNumber").text;
            var code = Refs.Get<TMP_InputField>("IF_VCode").text;
            if (string.IsNullOrEmpty(phoneNum) || string.IsNullOrEmpty(code))
            {
                UserInterface.Instance.ShowMessage(new MessagePanel(), "手机号和验证码不能为空");
                return;
            }

            waitHttp = true;
            string msg = default;// await WebRequestComponent.Instance.登录(phoneNum, code);
            waitHttp = false;

            var jsonData = Json.ToObject(msg);
            if (int.TryParse(jsonData["code"].ToString(), out int statu) && statu == 200)
            {
                UserInterface.Instance.ShowMessage(new MessagePanel(), "登录成功");

                //从接口拿到token   每次登录token都不一样 这是九灵token 用处暂时未知
                var accessToken = jsonData["data"]["access_token"].ToString();

                //手机号验证通过了  拿手机号去数据库查  服务端如果没有对应的数据  则自动创建账号并建立关联
                //数据库返回这个账号下的角色配置过来
                Login(phoneNum);
            }
            else
            {
                UserInterface.Instance.ShowMessage(new MessagePanel(), jsonData["msg"].ToString());
            }
            Log.Info("登录返回->" + msg);
        }
    }

    void Login(string phoneNum)
    {
        if (string.IsNullOrEmpty(phoneNum))
        {
            return;
        }
        Log.Info("登录-->" + phoneNum);
        TcpClientComponent.Instance.Send2ServerAsync(new C2S_账号登入()
        {
            PhoneNum = phoneNum
        });
    }

    /// <summary>
    /// 协议复选框事件
    /// </summary>
    /// <param name="isOn">复选框状态</param>
    private void T_Protocol(bool isOn)
    {
        AgreeAll = AgreePrivacy = AgreeUser = isOn;
    }

    /// <summary>
    /// 保持登录复选框事件
    /// </summary>
    /// <param name="isOn">复选框状态</param>
    private void T_KeepLogin(bool isOn)
    {
        KeepLoginState = isOn;
        int KeepLoginStateData = 0;
        if (isOn) KeepLoginStateData = 1;
        PlayerPrefs.SetInt("KeepLogin", KeepLoginStateData);
    }

    /// <summary>
    /// 保持登录状态检查事件
    /// </summary>
    private void KeepLoginCheck()
    {
        //初始化复选框相关变量
        AgreeAll = AgreePrivacy = AgreeUser = false;
        //初始化保持登录状态  
        if (PlayerPrefs.HasKey("KeepLogin"))
        {
            var _KeepLoginState = PlayerPrefs.GetInt("KeepLogin");
            if (_KeepLoginState == 0)
                KeepLoginState = false;
            else KeepLoginState = true;
            UIManager.Instance.UI_GetGameObject("T_KeepLogin").GetComponent<Toggle>().isOn = KeepLoginState;
        }
        else KeepLoginState = false;
    }

}
